import React from 'react';
import { Avatar, Box, Grid, IconButton, Tooltip, Typography } from '@mui/material';
import myPic from '../../assets/minha foto.jpeg';
import GitHubIcon from '@mui/icons-material/GitHub';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import WhatshotIcon from '@mui/icons-material/Whatshot';
import { useNavigate } from 'react-router-dom';

export const PresentationPage = () => {
   const navigate = useNavigate();

   const handleGithub = () => {
      navigate('/github');
   };

   const handleLinkedin = () => {
      navigate('/linkedin');
   };

   const handleFirebase = () => {

   };

   return (
      <Box sx={{
         backgroundColor: "rgba(0, 145, 136, 0.8)",
         paddingTop: "3%",
         paddingLeft: "10%",
         paddingRight: "12%",
         paddingBottom: "2%",
      }}>
         <Grid container direction="column">
            <Grid item>
               <Grid container direction="row" justifyContent="space-evenly">
                  <Grid item>
                     <Tooltip title="GitHub">
                        <IconButton onClick={handleGithub}>
                           <GitHubIcon focusable={true} sx={{ fontSize: "55px", color: "black" }} />
                        </IconButton>
                     </Tooltip>
                  </Grid>
                  <Grid item>
                     <Tooltip title="LinkedIn">
                        <IconButton onClick={handleLinkedin}>
                           <LinkedInIcon focusable={true} sx={{ fontSize: "55px", color: "black" }} />
                        </IconButton>
                     </Tooltip>
                  </Grid>
               </Grid>
            </Grid>
            <Grid item>
               <Grid container direction="row" justifyContent="space-between">
                  <Grid item paddingTop="6%">
                     <Typography sx={{
                        fontFamily: "Noto Sans",
                        fontWeight: 500,
                        fontSize: "36px"
                     }}>
                        Olá! Eu sou Bianca Sakaniwa,
                     </Typography>
                  </Grid>
                  <Grid item>
                     <Avatar src={myPic} variant="circular" sx={{ width: 150, height: 150 }} />
                  </Grid>
               </Grid>
            </Grid>
            <Grid item paddingTop="3%">
               <Typography sx={{
                  fontFamily: "Noto Sans",
                  fontWeight: 800,
                  fontSize: "62px",
                  textAlign: "end"
               }}>
                  Desenvolvedora de Software Fullstack
               </Typography>
            </Grid>
            <Grid item>
               <Typography sx={{
                  fontFamily: "Noto Sans",
                  fontWeight: 700,
                  fontSize: "38px",
                  textAlign: "end",
               }}>
                  Com ênfase em front-end
               </Typography>
            </Grid>
            <Grid item sx={{ width: "400px", paddingTop: "2%" }}>
               <Typography sx={{
                  fontFamily: "Noto Sans",
                  fontWeight: 500,
                  fontSize: "32px"
               }}>
                  Vem conhecer um pouco do meu trabalho!
               </Typography>
            </Grid>
            <Grid item paddingTop="2%" onClick={handleFirebase}>
               
            </Grid>
         </Grid>
      </Box>
   )
}
