import { Card, CardContent, Grid, Typography } from '@mui/material';
import React from 'react';

export const ProjectCard = ({ onClick, title, description, minWidth }) => {
   return (
      <Card sx={{ minWidth: minWidth, cursor: "pointer" }}>
         <CardContent sx={{ padding: "10%" }}>
            <Grid container direction="column">
               <Grid item paddingBottom="10%">
                  <Typography variant="h4" fontFamily="Noto Sans" fontWeight={600}>
                     {title}
                  </Typography>
               </Grid>
               <Grid item>
                  <Typography fontFamily="Noto Sans">{description}</Typography>
               </Grid>
            </Grid>
         </CardContent>
      </Card>
   )
}
