import React from 'react';
import { Box, Button, Card, CardActions, CardContent, Grid, Typography } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';

export const ProjectCard = ({ onClick, title, description, maxWidth }) => {
   const [isExpanded, setIsExpanded] = useState(false);
   return (
      <Card sx={{ maxWidth: maxWidth, cursor: "pointer" }}>
         <CardContent sx={{ padding: "10%", paddingBottom: "2%" }}>
            <Grid container direction="column">
               <Grid item paddingBottom="10%">
                  <Typography variant="h4" fontFamily="Noto Sans" fontWeight={600}>
                     {title}
                  </Typography>
               </Grid>
               <Grid item>
                  <Typography fontFamily="Noto Sans">{description}</Typography>
               </Grid>
            </Grid>
         </CardContent>
         <CardActions>
            <Box sx={{ display: "flex", justifyContent: "flex-end" }}>
               <Button size="small" endIcon={<ExpandMoreIcon />}>
                  Informações técnicas
               </Button>
            </Box>
         </CardActions>
      </Card>
   )
}
